    private void btn_CadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_CadastrarActionPerformed
        Fornecedor fornecedor = new Fornecedor();
        fornecedor.setNome(txt_Nome.getText());
        fornecedor.setNomeFantasia(txt_NomeFantasia.getText());
        fornecedor.setCnpj(txt_CNPJ.getText());
        fornecedor.setRua(txt_Rua.getText());
        fornecedor.setBairro(txt_Bairro.getText());
        fornecedor.setNumero(txt_Numero.getText());
        fornecedor.setCidade(txt_Cidade.getText());
        fornecedor.setCEP(txt_CEP.getText());
        fornecedor.setUF(txt_UF.getText());
        fornecedor.setEmail(txt_Email.getText());
        fornecedor.setTelefone(txt_Telefone.getText());

        FornecedorDAO fDAO = new FornecedorDAO();
        fDAO.inserirFornecedor(fornecedor);

        limparFormulario();
    }//GEN-LAST:event_btn_CadastrarActionPerformed

    private void btn_AtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_AtualizarActionPerformed
        Fornecedor fornecedor = new Fornecedor();
        fornecedor.setID(Integer.parseInt(txt_FornecedorID.getText()));
        fornecedor.setNome(txt_Nome.getText());
        fornecedor.setNomeFantasia(txt_NomeFantasia.getText());
        fornecedor.setCnpj(txt_CNPJ.getText());
        fornecedor.setRua(txt_Rua.getText());
        fornecedor.setBairro(txt_Bairro.getText());
        fornecedor.setNumero(txt_Numero.getText());
        fornecedor.setCidade(txt_Cidade.getText());
        fornecedor.setCEP(txt_CEP.getText());
        fornecedor.setUF(txt_UF.getText());
        fornecedor.setEmail(txt_Email.getText());
        fornecedor.setTelefone(txt_Telefone.getText());

        FornecedorDAO fDAO = new FornecedorDAO();
        fDAO.editarFornecedor(fornecedor);
        limparFormulario();
    }//GEN-LAST:event_btn_AtualizarActionPerformed

    private void btn_ConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_ConsultarActionPerformed
        int idFornecedor = Integer.parseInt(txt_ID.getText());
        FornecedorDAO fDAO = new FornecedorDAO();

        Fornecedor fornecedor = fDAO.getFornecedor(idFornecedor);
        if (fornecedor == null) {
            limparFormulario();
            JOptionPane.showMessageDialog(this, "Fornecedor  não encontrada!");
        } else {
            txt_ID.setText(String.valueOf(fornecedor.getID()));
            txt_FornecedorID.setText(String.valueOf(fornecedor.getID()));
            txt_Nome.setText(String.valueOf(fornecedor.getNome()));
            txt_NomeFantasia.setText(String.valueOf(fornecedor.getNomeFantasia()));
            txt_CNPJ.setText(String.valueOf(fornecedor.getCnpj()));
            txt_Rua.setText(String.valueOf(fornecedor.getRua()));
            txt_Numero.setText(String.valueOf(fornecedor.getNumero()));
            txt_Bairro.setText(String.valueOf(fornecedor.getBairro()));
            txt_Cidade.setText(String.valueOf(fornecedor.getCidade()));
            txt_CEP.setText(String.valueOf(fornecedor.getCEP()));
            txt_UF.setText(String.valueOf(fornecedor.getUF()));
            txt_Email.setText(String.valueOf(fornecedor.getEmail()));
            txt_Telefone.setText(String.valueOf(fornecedor.getTelefone()));
        }
    }//GEN-LAST:event_btn_ConsultarActionPerformed

    private void btn_DeletarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_DeletarActionPerformed
        int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir este Fornecedor?",
                "Exclusão", JOptionPane.YES_NO_OPTION);

        if (resposta == JOptionPane.YES_OPTION) {
            FornecedorDAO fDAO = new FornecedorDAO();
            fDAO.excluirFornecedor(Integer.parseInt(txt_ID.getText()));
            limparFormulario();
        }
    }//GEN-LAST:event_btn_DeletarActionPerformed

