    private void limparFormulario() {
        txt_Nome.setText("");
        txt_NomeFantasia.setText("");
        txt_CNPJ.setText("");
        txt_Rua.setText("");
        txt_Bairro.setText("");
        txt_Numero.setText("");
        txt_Cidade.setText("");
        txt_CEP.setText("");
        txt_UF.setText("");
        txt_Email.setText("");
        txt_Telefone.setText("");
        txt_ID.setText("");
        txt_FornecedorID.setText("");
    }

